//在编译阶段 jsx通过babel转化成React.createElement调用

// React.createElement(标签名，属性props对象， 子节点1，子节点2 …)
// 1. 第一个参数必填，另两个参数选填
// 2. 参数：标签名，属性对象，子节点
// 3. 返回值：虚拟DOM对象
// 比如 
// React.createElement('div', {id: 'app'}, [
//   React.createElement('p', {class: ''}, 'text')
// ])

const Comp = (
  <div id="comp">
    <p>text1</p>
    <p>text2</p>
    </div>
)
console.log(Comp)

// document.getElementById('root')

const render = (virtualDom,container) => {

  const {type, props} = virtualDom
  const dom = document.createElement(type) 
  Object.keys(props).filter(prop => prop !=='children').forEach(prop => {
    //将props 属性挂在dom结构上，但排除children属性
    // 注意 这是简单对属性处理，还需要对style 事件进行特色处理
    dom[prop] = props[prop]
  })
  // 如果children是数组，递归调用 render函数
  if(Array.isArray(props.children)) {
     props.children.forEach(child => render(child, dom))
  }
  container.appendChild(dom)
}

render(Comp, document.getElementById('root'))
